import React, { useState } from 'react'
import logo from './logo.svg';
import './App.css';
import Chessboard from 'chessboardjsx';
import Chess, { ChessInstance, ShortMove } from "chess.js";
import MoveHistory from './components/MoveHistory';

function App() {
  const [chess] = useState(new Chess("rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq - 0 1"));
  const [fen, setFen] = useState(chess.fen());
  const [movesHistory, setMovesHistory] = useState([]);
  const [selectedBot, setSelectedBot] = useState();

  const handlePlayerMove = (playerMove) => {
    // Checks if playermove is valid
    if (chess.move(playerMove)) {
      // add move to history
      const newMovesList = movesHistory;
      newMovesList.push(playerMove);
      setMovesHistory(newMovesList);

      // computer response
      const moves = chess.moves();

      // update chessboard
      setFen(chess.fen());
    }
  }
  return (
    <div className="app-container">
      <h1>Chess AI</h1>
      <div className="chess-container">
        <Chessboard
          width={400}
          position={fen}
          onDrop={(move) => handlePlayerMove({
            from: move.sourceSquare,
            to: move.targetSquare,
            promotion: "q",
          })}

        />
      </div>
      <div className="utility-container">
        <div className="move-history">

        </div>
        <div className="data-visualization">

        </div>
      </div>
    </div>
  );
}

export default App;
